content:
静态文本

wxStaticText小部件显示一行或多行只读文本。


statictext.h

    #include <wx/wx.h>

    class StaticText : public wxFrame
    {
    public:
     StaticText(const wxString& title);
    };

statictext.cpp

    #include "statictext.h"

    StaticText::StaticText(const wxString& title)
     : wxFrame(NULL, wxID_ANY, title)
    {
     wxPanel *panel = new wxPanel(this, wxID_ANY);
     wxString text = wxT("'Cause sometimes you feel tired,\n\
    feel weak, and when you feel weak,\
    you feel like you wanna just give up.\n\
    But you gotta search within you,\
    you gotta find that inner strength\n\
    and just pull that shit out of you\
    and get that motivation to not give up\n\
    and not be a quitter,\
    no matter how bad you wanna just fall flat on your face and collapse.");

     wxStaticText *st = new wxStaticText(panel, wxID_ANY, text, 
     wxPoint(10, 10), wxDefaultSize, wxALIGN_CENTRE);

     this->SetSize(600, 110);
     this->Centre();
    }

main.h

    #include <wx/wx.h>

    class MyApp : public wxApp
    {
     public:
     virtual bool OnInit();
    };

main.cpp

    #include "main.h"
    #include "statictext.h"

    IMPLEMENT_APP(MyApp)

    bool MyApp::OnInit()
    {
     StaticText *st = new StaticText(wxT("StaticText"));
     st->Show(true);
     return true;
    }

CMakeLists.txt

    cmake_minimum_required(VERSION 3.16)
    project(statictext)

    set(CMAKE_CXX_STANDARD 14)

    add_definitions(
            -DwxMSVC_VERSION_AUTO=1
            -DwxMSVC_VERSION_COMPAT=1
    )

    include_directories(
            $ENV{WXWIN}/include
            $ENV{WXWIN}/lib/vc14x_lib/mswud
    )

    link_directories(
            $ENV{WXWIN}/lib/vc14x_lib
    )

    add_executable(
            ${PROJECT_NAME}
            main.cpp main.h
            statictext.cpp statictext.h)

    set_target_properties(
            ${PROJECT_NAME}
            PROPERTIES
            MSVC_RUNTIME_LIBRARY "MultiThreaded$<$<CONFIG:Debug>:Debug>"
            WIN32_EXECUTABLE true
    )

    target_link_libraries(
