 spine状态切换规则
 toIdle() {
     switch (this.curState) {
            case State.HUI_FU:
            case State.RUN_LEFT:
            case State.RUN_RIGHT:
            case State.FALL_DOWN:
                this.idle();
                break;
        }
 }

 toRunLeft(){
   switch (this.curState) {
            case State.HUI_FU:
            case State.IDLE:
            case State.RUN_RIGHT:
                runLeft();
                break;
        }
   }


toRunRight() {
    switch (this.curState) {
            case State.HUI_FU:
            case State.IDLE:
            case State.RUN_LEFT:
              this.runRight();
                break;
        }
}

 toJumpUp() {
     switch (this.curState) {
            case State.IDLE:
            case State.RUN_LEFT:
            case State.RUN_RIGHT:
                this.jumpUp();
                break;
        }
 }


  toFallDown() {
      switch (this.curState) {
            case State.JUMP_UP:
               this.fallDown();
                break;
        }  
   }


   toDie() {
switch (this.curState) {


            case State.RUN_LEFT:

            case State.RUN_RIGHT:
            case State.FALL_DOWN:
              this.death();
              break;
        }
