状态转换函数
      updateState() {
        let x = this.joystick.dir.x;
        let y = this.joystick.dir.y;

        if (x === 0 && y === 0) {
            if (this.isAttackStart) {
                this.attackDown();
            } else {
                this.idle();
            }
        } else if (-Math.PI / 4 < Math.atan2(y, x) && Math.atan2(y, x) <= Math.PI / 4) {  //向右
            if (this.isAttackStart) {
                this.attackRight();
            } else {
                this.moveRight();
            }
        } else if (Math.PI / 4 < Math.atan2(y, x) && Math.atan2(y, x) <= Math.PI * 3 / 4) { // 向上
            if (this.isAttackStart) {
                this.attackUp();
            } else {
                this.moveUp();
            }
        } else if (-Math.PI * 3 / 4 < Math.atan2(y, x) && Math.atan2(y, x) <= -Math.PI / 4) { // 向下
            if (this.isAttackStart) {
                this.attackDown()
            } else {
                this.moveDown();
            }
        } else {  // 向左
            if (this.isAttackStart) {
                this.attackLeft();
            } else {
                this.moveLeft();
            }
        }
    }
