查找框布局

在下面的例子中，我们将介绍几个重要的概念。

findclass.h

    #ifndef FINDLAYOUT_FINDCLASS_H
    #define FINDLAYOUT_FINDCLASS_H

    #include "wx/wx.h"

    class FindClass : public wxFrame {
    public:
        FindClass(const wxString& title);
    };


    #endif //FINDLAYOUT_FINDCLASS_H



findclass.cpp

    #include "findclass.h"

    FindClass::FindClass(const wxString &title)
        :wxFrame(nullptr, -1, title, wxPoint(-1, -1), wxSize(450, 400))
    {
        wxPanel *panel = new wxPanel(this, -1);

        wxBoxSizer* vbox = new wxBoxSizer(wxVERTICAL);

        wxBoxSizer* hbox1 = new wxBoxSizer(wxHORIZONTAL);
        wxStaticText* st1 = new wxStaticText(panel, wxID_ANY, wxT("类名"));

        hbox1->Add(st1, 0, wxRight, 8);
        wxTextCtrl* tc = new wxTextCtrl(panel, wxID_ANY);
        hbox1->Add(tc, 1);
        vbox->Add(hbox1, 0, wxEXPAND | wxLEFT | wxRIGHT | wxTOP, 10);

        vbox->Add(-1, 10);

        wxBoxSizer* hbox2 = new wxBoxSizer(wxHORIZONTAL);
        wxStaticText* st2 = new wxStaticText(panel, wxID_ANY, wxT("匹配的类"));
        hbox2->Add(st2, 0);
        vbox->Add(hbox2, 0, wxLEFT | wxTOP, 10);

        vbox->Add(-1, 10);

        wxBoxSizer* hbox3 = new wxBoxSizer(wxHORIZONTAL);
        wxTextCtrl* tc2 = new wxTextCtrl(panel, wxID_ANY, wxT(""), wxPoint(-1, -1), wxSize(-1, -1), wxTE_MULTILINE);

        hbox3->Add(tc2, 1, wxEXPAND);
        vbox->Add(hbox3, 1, wxLEFT | wxRIGHT | wxEXPAND, 10);
        vbox->Add(-1, 25);

        wxBoxSizer* hbox4 = new wxBoxSizer(wxHORIZONTAL);
        wxCheckBox* cb1 = new wxCheckBox(panel, wxID_ANY, wxT("区分大小写"));

        hbox4->Add(cb1);
        wxCheckBox* cb2 = new wxCheckBox(panel, wxID_ANY, wxT("嵌套类"));

        hbox4->Add(cb2, 0, wxLEFT, 10);
        wxCheckBox* cb3 = new wxCheckBox(panel, wxID_ANY, wxT("非空项目类"));

        hbox4->Add(cb3, 0, wxLEFT, 10);
        vbox->Add(hbox4, 0, wxLEFT, 10);

        vbox->Add(-1, 25);

        wxBoxSizer* hbox5 = new wxBoxSizer(wxHORIZONTAL);
        wxButton* btn1 = new wxButton(panel, wxID_ANY, wxT("Ok"));
        hbox5->Add(btn1, 0);

        wxButton* btn2 = new wxButton(panel, wxID_ANY, wxT("Close"));
        hbox5->Add(btn2, 0, wxLEFT | wxBOTTOM, 5);
        vbox->Add(hbox5, 0, wxALIGN_RIGHT | wxRIGHT, 10);

        panel->SetSizer(vbox);

        this->Center();
    }




main.h

    #ifndef FINDLAYOUT_MAIN_H
    #define FINDLAYOUT_MAIN_H

    #include "wx/wx.h"

    class MyApp : public wxApp {
    public:
        virtual bool OnInit();
    };

    #endif //FINDLAYOUT_MAIN_H



main.cpp

    #include "main.h"
    #include "findclass.h"

    wxIMPLEMENT_APP(MyApp);

    bool MyApp::OnInit() {
        FindClass *frame = new FindClass(wxT("Find Class"));
        frame->Show(true);

        return wxAppConsoleBase::OnInit();
    }



CMakeLists.txt

    cmake_minimum_required(VERSION 3.16)
    project(FindLayout)

    set(CMAKE_CXX_STANDARD 14)

    add_definitions(
            -DwxMSVC_VERSION_AUTO=1
            -DwxMSVC_VERSION_COMPAT=1
            -DUNICODE=1
    )

    include_directories(
            $ENV{WXWIN}/include
            $ENV{WXWIN}/lib/vc14x_lib/mswud
    )

    link_directories(
            $ENV{WXWIN}/lib/vc14x_lib
    )

    add_executable(${PROJECT_NAME}
            main.h main.cpp
            findclass.h findclass.cpp
            )

    if (CMAKE_SYSTEM_NAME STREQUAL "Windows")
        set_property(
                TARGET ${PROJECT_NAME} PROPERTY
                WIN32_EXECUTABLE true
        )
    endif ()

    set_property(
            TARGET ${PROJECT_NAME} PROPERTY
            MSVC_RUNTIME_LIBRARY "MultiThreaded$<$<CONFIG:Debug>:Debug>"
    )

    target_link_libraries(
            ${PROJECT_NAME}
            shlwapi.lib
            version.lib
            comctl32.lib
            rpcrt4.lib
            ws2_32.lib
            wininet.lib
